import "core-js/modules/es.array.push.js";
import AdminAdd from '../components/AdminAdd.vue';
import { api } from '../helpers/Helpers.js';
export default {
  name: "admin-page",
  components: {
    'add-form': AdminAdd
  },
  methods: {
    createOrUpdate(item) {
      if (item.id) {
        api.put(`/api/cameras/${item.id}`, item).then(() => {
          this.$router.push({
            name: 'admin-page'
          });
        });
      } else {
        api.post('/api/cameras', item).then(() => {
          this.$router.push({
            name: 'admin-page'
          });
        });
      }
    }
  }
};
